export default {
  name: "User",
  data: function data() {
    return {
      tableData: [],
      total: 0,
      pageNum: 1,
      pageSize: 10,
      name: "",
      form: {},
      dialogFormVisible: false,
      multipleSelection: [],
      menuVisible: false,
      menuData: [],
      props: {
        label: 'name'
      },
      expends: [],
      checks: [],
      roleId: 0,
      roleFlag: ""
    };
  },
  created: function created() {
    this.load();
  },
  methods: {
    load: function load() {
      var _this = this;

      // 详情请看main.js
      this.request.get("/role/page", {
        params: {
          pageNum: this.pageNum,
          pageSize: this.pageSize,
          name: this.name
        }
      }).then(function (res) {
        console.log(res);
        _this.tableData = res.data.records;
        _this.total = res.data.total;
      });
    },
    save: function save() {
      var _this2 = this;

      this.request.post("/role/save", this.form).then(function (res) {
        if (res.data) {
          _this2.$message.success("保存成功");

          _this2.dialogFormVisible = false;

          _this2.load();
        } else {
          _this2.$message.error("保存失败");
        }
      });
    },
    del: function del(id) {
      var _this3 = this;

      this.request.delete("/role/" + id).then(function (res) {
        if (res.data) {
          _this3.$message.success("删除成功");

          _this3.pageNum = 1;

          _this3.load();
        } else {
          _this3.$message.error("删除失败");
        }
      });
    },
    handleSelectionChange: function handleSelectionChange(val) {
      console.log(val);
      this.multipleSelection = val;
    },
    delBatch: function delBatch() {
      var _this4 = this;

      var ids = this.multipleSelection.map(function (v) {
        return v.id;
      }); // [{}, {}, {}] => [1,2,3]

      this.request.post("/role/del/batch", ids) // 这里使用delete会报错
      .then(function (res) {
        if (res.data) {
          _this4.$message.success("批量删除成功");

          _this4.pageNum = 1;

          _this4.load();
        } else {
          _this4.$message.error("批量删除失败");
        }
      });
    },
    reset: function reset() {
      this.name = "";
      this.load();
    },
    handleAdd: function handleAdd() {
      this.dialogFormVisible = true;
      this.form = {};
    },
    selectMenu: function selectMenu(roleId) {
      var _this5 = this;

      this.roleId = roleId;
      this.request.get("/menu/all").then(function (res) {
        _this5.menuData = res.data; // 把后台返回的菜单数据处理成 id数组

        _this5.expends = _this5.menuData.map(function (v) {
          return v.id;
        });
      });
      this.request.get("/role/roleMenu/" + this.roleId).then(function (res) {
        _this5.menuVisible = true;
        _this5.checks = res.data;
      });
    },
    saveRoleMenu: function saveRoleMenu() {
      var _this6 = this;

      this.request.post("/role/roleMenu/" + this.roleId, this.$refs.tree.getCheckedKeys()).then(function (res) {
        if (res.code === '200') {
          _this6.$message.success("绑定成功");

          _this6.menuVisible = false;

          if (_this6.roleFlag === 'ROLE_ADMIN') {
            _this6.$store.commit('logout');

            _this6.$router.replace('/login');
          }
        } else {
          _this6.$message.error("绑定失败");
        }

        console.log(res);
      });
    },
    handleEdit: function handleEdit(row) {
      this.form = JSON.parse(JSON.stringify(row)); //可以解决编辑未确认就改变的问题

      this.dialogFormVisible = true;
    },
    handleSizeChange: function handleSizeChange(pageSize) {
      console.log(pageSize);
      this.pageSize = pageSize;
      this.load();
    },
    handleCurrentChange: function handleCurrentChange(pageNum) {
      console.log(pageNum);
      this.pageNum = pageNum;
      this.load();
    }
  }
};
