import * as echarts from 'echarts';
export default {
  name: "Data",
  mounted: function mounted() {
    var option = {
      title: {
        text: '各季度员工数量统计',
        subtext: '趋势图',
        left: 'center'
      },
      tooltip: {
        trigger: 'item'
      },
      legend: {
        orient: 'vertical',
        left: 'left'
      },
      xAxis: {
        type: 'category',
        data: ["第一季度", "第二季度", "第三季度", "第四季度"]
      },
      yAxis: {
        type: 'value'
      },
      series: [{
        data: [],
        type: 'line'
      }, {
        data: [],
        type: 'bar',
        name: '柱状'
      }, {
        data: [5, 7, 3, 4],
        type: 'line'
      }, {
        data: [5, 7, 3, 4],
        type: 'bar',
        name: '虚拟柱状'
      }]
    };
    var chartDom = document.getElementById('main');
    var myChart = echarts.init(chartDom);
    this.request.get('/echarts/members').then(function (res) {
      // 填空
      // option.xAxis.data = res.data.x
      option.series[0].data = res.data;
      option.series[1].data = res.data; // 数据准备完毕之后再set

      myChart.setOption(option);
    });
    myChart.setOption(option);
    var pieOption = {
      title: {
        text: '各季度员工数量统计',
        subtext: '比例图',
        left: 'center'
      },
      tooltip: {
        trigger: 'item'
      },
      legend: {
        orient: 'vertical',
        left: 'left'
      },
      series: [{
        type: 'pie',
        radius: '60%',
        label: {
          //饼图图形上的文本标签
          normal: {
            show: true,
            position: 'inner',
            //标签的位置
            textStyle: {
              fontWeight: 300,
              fontSize: 14,
              //文字的字体大小
              color: "#fff"
            },
            formatter: '{d}%'
          }
        },
        data: [],
        // 填空
        emphasis: {
          itemStyle: {
            shadowBlur: 10,
            shadowOffsetX: 0,
            shadowColor: 'rgba(0, 0, 0, 0.5)'
          }
        }
      }]
    };
    var pieDom = document.getElementById('pie');
    var pieChart = echarts.init(pieDom);
    this.request.get("/echarts/members").then(function (res) {
      pieOption.series[0].data = [{
        name: "第一季度",
        value: res.data[0]
      }, {
        name: "第二季度",
        value: res.data[1]
      }, {
        name: "第三季度",
        value: res.data[2]
      }, {
        name: "第四季度",
        value: res.data[3]
      }];
      pieChart.setOption(pieOption);
    });
  }
};
